import {
  PieChart,
  Pie,
  Legend,
  Tooltip,
  ResponsiveContainer,
  Cell,
  Label,
} from "recharts";

const Piechart = () => {
  const data01 = [
    { name: "Food", value: 400, color: "red" },
    { name: "Entertainment", value: 300, color: "blue" },
    { name: "Travel", value: 300, color: "yellow" },
  ];


  
  const totalValue = data01.reduce((total, current) => total + current.value, 0);

  return (
    <div>
      {/* <ResponsiveContainer width="100%" height="100%"> */}
      <PieChart width={300} height={250}>
        <Pie
          dataKey="value"
          data={data01}
          cx="50%"
          cy="50%"
          outerRadius={80}
          labelLine={false}
          label
        >
          {data01.map((entry, index) => (
            <Cell key={`cell-${index}`} fill={entry.color} />
          ))}
          {data01.map((entry, index) => {
            const startAngle = 0;
            const endAngle = 360 * (entry.value / totalValue);
            return(
            <Label
              key={`label-${index}`}
              position="inside"
              fill="#fff"
              textAnchor="middle"
            >
              {`${(
                (entry.value /
                  data01.reduce((total, current) => total + current.value, 0)) *
                100
              ).toFixed(2)}%`}
            </Label>
          )})}
        </Pie>
        <Tooltip />
        <Legend layout="horizontal" align="left" verticalAlign="bottom" />
      </PieChart>
      {/* </ResponsiveContainer> */}
    </div>
  );
};

export default Piechart;
