import React, { useEffect, useState } from "react";
import { Modal, TextField, Button, Select, MenuItem } from "@mui/material";

const IncomeModal = ({ open, onClose, setWalletBalance, setUserData }) => {
  const [amount, setAmount] = useState(null);

  const handleAddBalance = () => {
    const parsedAmount = parseFloat(amount);
    if (!isNaN(parsedAmount)) {
      setUserData(prevData => ({ ...prevData, income: prevData.income + parsedAmount }));
      onClose();
    } else {
      alert("Please enter a valid amount for income.");
    }
    onClose();
  };


  return (
    <Modal
      open={open}
      onClose={onClose}
      sx={{
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        backgroundColor: "rgba(255, 255, 255, 0.77)",
      }}
    >
      <div
        style={{
          backgroundColor: "rgba(217, 217, 217, 1)",
          padding: "0 0 20px 20px",
          width: "538px",
          height: "164px",
          borderRadius: "20px",
          justifyContent: "center",
          alignItems: "center",
        }}
      >
        <h3 style={{ fontWeight: "700", fontSize: "30px", padding: 0 }}>
          Add Balance
        </h3>
        <TextField
          size="small"
          variant="outlined"
          placeholder="Income Amount"
          onChange={(e) => setAmount(e.target.value)}
          sx={{
            borderRadius: "15px",
            backgroundColor: "rgba(251, 251, 251, 1)",
            overflow: "hidden",
          }}
        />
        <Button
          variant="contained"
          sx={{
            textTransform: "none",
            fontWeight: "400",
            fontSize: "16px",
            color: "#000",
            borderBottom: "1px solid red",
            backgroundColor: "rgba(217, 217, 217, 1)",
            borderRadius: "10px",
            margin: "0 1rem 0 1rem",
            ":hover": {
              backgroundColor: "rgba(244, 187, 74, 1)",
              color: "#fff",
            },
          }}
          onClick={handleAddBalance}
        >
          Add Balance
        </Button>
        <Button
          variant="contained"
          sx={{
            textTransform: "none",
            fontWeight: "400",
            fontSize: "16px",
            color: "#000",
            borderBottom: "1px solid red",
            backgroundColor: "rgba(217, 217, 217, 1)",
            borderRadius: "10px",
            ":hover": {
              backgroundColor: "rgba(244, 187, 74, 1)",
              color: "#fff",
            },
          }}
          onClick={onClose}
        >
          Cancel
        </Button>
      </div>
    </Modal>
  );
};

const ExpenseModal = ({ open, onClose, setWalletBalance, walletBalance, setUserData }) => {
  const [title, setTitle] = useState("");
  const [amount, setAmount] = useState("");
  const [category, setCategory] = useState("food");
  const [date, setDate] = useState("");
  
  const handleSubtractBalance = () => {
    if (!title.trim() || !amount || !date) {
      alert("Please fill in all fields.");
      return;
    }
    
    const parsedAmount = parseFloat(amount);
    const parsedDate = new Date(date).toISOString().slice(0, 10);
    
    
    if (isNaN(parsedAmount) || parsedAmount <= 0 || !parsedDate) {
      alert("Please enter valid data for the expense.");
      return;
    }

    if (parsedAmount > walletBalance) {
      alert("You do not have sufficient Wallet Balance.");
      return;
    }

    const newExpense = {
      title: title.trim(),
      amount: parsedAmount,
      category: category,
      date: parsedDate,
    };

    setUserData(prevData => ({
      ...prevData,
      expenses: [...prevData.expenses, newExpense],
      income: prevData.income - parsedAmount,
    }));

    onClose();
  }
 
  return (
    <Modal
      open={open} onClose={onClose}
      sx={{display: "flex",alignItems: "center",justifyContent: "center", backgroundColor: "rgba(255, 255, 255, 0.77)",}}
    >
      <div
        style={{backgroundColor: "rgba(217, 217, 217, 1)",padding: "0 0 20px 20px", width: "538px",height: "auto",borderRadius: "20px",}}
      >
        <h3>Add Expenses</h3>
        <div>
          <div style={{display: "flex",justifyContent: "space-around",alignItems: "flex-start", }}>
            <TextField size="small" label="Title" variant="outlined" placeholder="Title" value={title} onChange={(e) => setTitle(e.target.value)}
              sx={{width: "14rem", marginBottom: "0.5rem",borderRadius: "15px", backgroundColor: "rgba(251, 251, 251, 1)",borderBottom: "3px solid #b9b9b9",borderLeft: "1px solid #b9b9b9",}}
            />
            <TextField
              size="small" variant="outlined" type="number" placeholder="Price" value={amount} onChange={(e) => setAmount(e.target.value)}
              sx={{
                width: "14rem", marginbo: "0.5rem", borderRadius: "15px", backgroundColor: "rgba(251, 251, 251, 1)",  overflow: "hidden", borderBottom: "3px solid #b9b9b9", borderLeft: "1px solid #b9b9b9",
              }}
            />
          </div>
          <div style={{  display: "flex", justifyContent: "space-around",alignItems: "flex-start", }}>
            <Select
              placeholder="Select Category"
              value={category}
              onChange={(e) => setCategory(e.target.value)}
              sx={{ width: "14rem", height: "2.7rem", backgroundColor: "rgba(251, 251, 251, 1)", color: "#000", borderRadius: "15px", borderBottom: "3px solid #b9b9b9", borderLeft: "1px solid #b9b9b9",}}
>
              <MenuItem value="Food">Food</MenuItem>
              <MenuItem value="Entertainment">Entertainment</MenuItem>
              <MenuItem value="Travel">Travel</MenuItem>
            </Select>
            <TextField
              type="date"  size="small" variant="outlined"  placeholder="Title"  value={date} onChange={(e) => setDate(e.target.value)}
              sx={{  width: "14rem", marginBottom: "0.5rem", borderRadius: "15px", backgroundColor: "rgba(251, 251, 251, 1)",  overflow: "hidden", color: "#b9b9b9", borderBottom: "3px solid #b9b9b9", borderLeft: "1px solid #b9b9b9", }}
            />
          </div>
          <div style={{ display: "flex", justifyContent: "space-around",  alignItems: "flex-start", }} >
            <Button
              variant="contained"
              sx={{ width: "14rem", textTransform: "none", fontWeight: "400", fontSize: "16px", color: "#000", borderBottom: "1px solid red", backgroundColor: "rgba(217, 217, 217, 1)", borderRadius: "10px", ":hover": { backgroundColor: "rgba(244, 187, 74, 1)", color: "#fff", },}}
              onClick={handleSubtractBalance}
            >
              Add Expense
            </Button>
            <Button
              variant="contained"
              sx={{ width: "14rem", textTransform: "none", fontWeight: "400", fontSize: "16px", color: "#000", borderBottom: "1px solid red", backgroundColor: "rgba(217, 217, 217, 1)",  borderRadius: "10px", ":hover": { backgroundColor: "rgba(244, 187, 74, 1)", color: "#fff", }, }}
              onClick={onClose}
            >
              Cancel
            </Button>
          </div>
        </div>
      </div>
    </Modal>
  );
};

export { IncomeModal, ExpenseModal };
