import {
  PieChart,
  Pie,
  Legend,
  Tooltip,
  ResponsiveContainer,
  Cell,
  Label,
} from "recharts";

const Piechart = () => {
  const data01 = [
    { name: "Food", value: 400, color: "red" },
    { name: "Entertainment", value: 300, color: "blue" },
    { name: "Travel", value: 300, color: "yellow" },
  ];

  const COLORS = ['#0088FE', '#00C49F', '#FFBB28',];
  const RADIAN = Math.PI / 180;
  const totalValue = data01.reduce((total, current) => total + current.value, 0);
  const cx = 200; 
  const cy = 200; 

  return (
    <div>
      {/* <ResponsiveContainer width="100%" height="100%"> */}
      <PieChart width={300} height={250}>
        <Pie
          dataKey="value"
          data={data01}
          cx="50%"
          cy="50%"
          outerRadius={80}
          labelLine={false}
          label
        >
          {data01.map((entry, index) => (
            <Cell key={`cell-${index}`} fill={entry.color} />
          ))}
          {data01.map((entry, index) => {
            const startAngle = 0;
            const endAngle = 360 * (entry.value / totalValue);
            const midAngle = startAngle + (endAngle - startAngle) / 2;
              const radius = 80;
              const x = cx + radius * Math.cos(-midAngle * (Math.PI / 180));
              const y = cy + radius * Math.sin(-midAngle * (Math.PI / 180));
              return (
                <Label
                  key={`label-${index}`}
                  position="inside"
                  fill="#fff"
                  textAnchor="middle"
                  angle={midAngle}
                  x={x}
                  y={y}
                >
                  {`${((entry.value / totalValue) * 100).toFixed(2)}%`}
                </Label>
          )})}
        </Pie>
        <Tooltip />
        <Legend layout="horizontal" align="left" verticalAlign="bottom" />
      </PieChart>
      {/* </ResponsiveContainer> */}
    </div>
  );
};

export default Piechart;
