import { Button, Modal, TextField, Typography } from "@mui/material";
import Card from "@mui/material/Card";
import Piechart from "./Piechart";
import { IncomeModal } from "./Modals";
import { useState } from "react";

const Dashboard = ({ walletBalance, handleOpenIncomeModal }) => {
    const IncomeModal = ({ openIncome, handleCloseIncomeModal }) => {
        const handleSubmit = () => {
          handleCloseIncomeModal();
        };
      
        const [amount, setAmount] = useState("");
        return (
          <Modal open={openIncome} onClose={handleSubmit}>
            <h3>Add Balance</h3>
            <TextField onChange={(e) => setAmount(e.target.value)} />
            <Button>Add Balance</Button>
            <Button onClick={handleSubmit}>Cancel</Button>
          </Modal>
        );
      };
      
      const ExpenseModal = () => {
        return <div></div>;
      };
  return (
    <div>
      <div
        className="top_section"
        style={{
          backgroundColor: "#626262",
          display: "flex",
          justifyContent: "space-evenly",
          alignItems: "center",
          padding: "10px",
          borderRadius: "15px",
          height: "15rem",
        }}
      >
        <Card
          className="income_card"
          sx={{
            width: "355.41px",
            height: "181px",
            backgroundColor: "#9b9b9b",
            color: "#fff",
            display: "flex",
            justifyContent: "center",
            flexDirection: "column",
            alignItems: "center",
          }}
        >
          <Typography
            sx={{
              fontSize: "30px",
              fontWeight: "400",
              lineHeight: "34.47px",
              paddingBottom: "20px",
            }}
          >
            Wallet Balance:{" "}
            <span style={{ color: "rgba(157, 255, 91, 1)", fontWeight: "700" }}>
              &#x20B9; ${walletBalance}
            </span>
          </Typography>
          <Button
            sx={{
              width: "80%",
              color: "#fff",
              backgroundColor: "rgba(181, 220, 82, 1)",
              borderRadius: "15px",
            }}
            onClick={handleOpenIncomeModal}
          >
            + Add Income
          </Button>
        </Card>
        

        <Card
          className="expense_card"
          sx={{
            width: "355.41px",
            height: "181px",
            backgroundColor: "#9b9b9b",
            color: "#fff",
            display: "flex",
            justifyContent: "center",
            flexDirection: "column",
            alignItems: "center",
          }}
        >
          <Typography
            sx={{
              fontSize: "30px",
              fontWeight: "400",
              lineHeight: "34.47px",
              paddingBottom: "20px",
            }}
          >
            Expense:{" "}
            <span style={{ color: "rgba(157, 255, 91, 1)", fontWeight: "700" }}>
              &#x20B9; 500
            </span>
          </Typography>
          <Button
            sx={{
              width: "80%",
              color: "#fff",
              backgroundColor: "rgba(181, 220, 82, 1)",
              borderRadius: "15px",
            }}
          >
            + Add Expense
          </Button>
        </Card>
        <Piechart />
      </div>
    </div>
  );
};

export default Dashboard;
