import React from 'react';
import { Bar, BarChart, Cell, Legend, Tooltip, XAxis, YAxis } from 'recharts';

const Barchart = ({userData, activeIndex }) => {
    const sortedExpenses = [...userData.expenses].sort((a, b) => b.amount - a.amount);


    return (
        <div>
            <BarChart width={250} height={250} data={sortedExpenses}>
           
                <YAxis  hide={true}/>
                <XAxis hide={true}type="category" dataKey="category"  />
                <Tooltip />
                
                <Bar dataKey="amount" fill="#8884d8" baseValue={0}>
                    {sortedExpenses.map((entry, index) => (
                        <Cell cursor="pointer" fill={index === activeIndex ? '#82ca9d' : '#8884d8'} key={`cell-${index}`} />
                    ))}
                </Bar>
            </BarChart>
        </div>
    );
};

export default Barchart;
