import { Button, Typography } from "@mui/material";
import Card from "@mui/material/Card";
import Piechart from "./Piechart";
import { useState } from "react";
import { ExpenseModal, IncomeModal } from "./Modals";

const Dashboard = () => {
  // Modals useStates
  const [openIncome, setOpenIncome] = useState(false);

  const handleOpenIncomeModal = () => {
    setOpenIncome(true);
  };

  const handleCloseIncomeModal = () => {
    setOpenIncome(false);
  };

  const [openExpense, setOpenExpense] = useState(false);

  const handleOpenExpenseModal = () => {
    setOpenExpense(true);
  };

  const handleCloseExpenseModal = () => {
    setOpenExpense(false);
  };
  // Modals useStates Ends here

  const [walletBalance, setWalletBalance] = useState(5000);
  const handleIncreaseBalance = (amount) => {
    amount.preventDefault();
    const parsedAmount = parseFloat(amount);
    if (!isNaN(parsedAmount)) {
      setWalletBalance((prevBalance) => prevBalance + parsedAmount);
    } else {
      alert("Please enter a valid amount for income.");
    }
    console.log(amount);
  };
  const handleSpendBalance = (amount) => {
    if (amount > walletBalance) {
      alert("You cannot spend more than your available balance!");
    } else {
      setWalletBalance((prevBalance) => prevBalance - parseFloat(amount));
    }
  };

  return (
    <div>
      <div
        className="top_section"
        style={{
          backgroundColor: "#626262",
          display: "flex",
          justifyContent: "space-evenly",
          alignItems: "center",
          padding: "10px",
          borderRadius: "15px",
          height: "15rem",
        }}
      >
        <Card
          className="income_card"
          sx={{
            width: "355.41px",
            height: "181px",
            backgroundColor: "#9b9b9b",
            color: "#fff",
            display: "flex",
            justifyContent: "center",
            flexDirection: "column",
            alignItems: "center",
          }}
        >
          <Typography
            sx={{
              fontSize: "30px",
              fontWeight: "400",
              lineHeight: "34.47px",
              paddingBottom: "20px",
            }}
          >
            Wallet Balance:{" "}
            <span style={{ color: "rgba(157, 255, 91, 1)", fontWeight: "700" }}>
              &#x20B9; {walletBalance}
            </span>
          </Typography>
          <Button
            sx={{
              width: "80%",
              color: "#fff",
              backgroundColor: "rgba(181, 220, 82, 1)",
              borderRadius: "15px",
            }}
            onClick={handleOpenIncomeModal}
          >
            + Add Income
          </Button>
        </Card>

        <Card
          className="expense_card"
          sx={{
            width: "355.41px",
            height: "181px",
            backgroundColor: "#9b9b9b",
            color: "#fff",
            display: "flex",
            justifyContent: "center",
            flexDirection: "column",
            alignItems: "center",
          }}
        >
          <Typography
            sx={{
              fontSize: "30px",
              fontWeight: "400",
              lineHeight: "34.47px",
              paddingBottom: "20px",
            }}
          >
            Expense:{" "}
            <span style={{ color: "rgba(157, 255, 91, 1)", fontWeight: "700" }}>
              &#x20B9; 500
            </span>
          </Typography>
          <Button
            sx={{
              width: "80%",
              color: "#fff",
              backgroundColor: "rgba(181, 220, 82, 1)",
              borderRadius: "15px",
            }}
            onClick={handleOpenExpenseModal}
          >
            + Add Expense
          </Button>
        </Card>
        <Piechart />
      </div>

      <IncomeModal
        open={openIncome}
        onClose={handleCloseIncomeModal}
        handleIncreaseBalance={handleIncreaseBalance}
      />
      <ExpenseModal open={openExpense} onClose={handleCloseExpenseModal} />
    </div>
  );
};

export default Dashboard;
