import React from 'react';
import { Bar, BarChart, Cell, Legend, Tooltip, XAxis, YAxis } from 'recharts';

const Barchart = ({userData, activeIndex }) => {

    return (
        <div>
            <BarChart width={350} height={250} data={userData.expenses}>
            {/* <XAxis dataKey="category" /> */}
                <YAxis dataKey="category"/>
                <Tooltip />
                <Legend />
                <Bar dataKey="amount" fill="#8884d8">
                    {userData.expenses.map((entry, index) => (
                        <Cell cursor="pointer" fill={index === activeIndex ? '#82ca9d' : '#8884d8'} key={`cell-${index}`} />
                    ))}
                </Bar>
            </BarChart>
        </div>
    );
};

export default Barchart;
